import { Business } from './../../_shared/model/model';
import { Router } from '@angular/router';
import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs/internal/Observable';
import { Store, select } from '@ngrx/store';

@Component({
  selector: 'app-view',
  templateUrl: './view.component.html',
  styleUrls: ['./view.component.scss'],
})
export class ViewComponent implements OnInit {
  data$!: Observable<any>;
  constructor(
    private route: Router,
    private store: Store<{ business: Business }>
  ) {}

  ngOnInit(): void {
    this.data$ = this.store.pipe(select('business'));
  }

  goToCreate() {
    return this.route.navigateByUrl('/home/create');
  }
}
<div class="container" style="background-color: white">
  <div class="row justify-content-between mb-3">
    <div class="row mt-3">
      <div class="col-lg-4">
        <input type="text" class="form-control" placeholder="Search Customer" />
      </div>
      <div class="col-lg-8 text-right d-flex justify-content-end">
        <button
          type="button"
          class="btn btn-outline-primary"
          (click)="goToCreate()"
        >
          Add New Customer
        </button>
      </div>
    </div>
  </div>
  <div class="card" *ngIf="data$ | async as items">
    <div class="table-responsive">
      <table class="table">
        <thead style="background-color: whitesmoke">
          <tr>
            <td>Customer Name</td>
            <td>Business Name</td>
            <td>Address</td>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let item of items">
            <td *ngFor="let x of item.contactinfo">{{ x.name }}</td>
            <td>{{ item.businessName }}</td>
            <td>{{ item.address }}</td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
  <br />
</div>
